ruleSearch.bookList:
.bookList li
<js>
result.toArray().map(o=>{
    var detail = String(o.select('a:eq(0)').attr('href')).replace(/^\//, 'http://www.tadu.com/');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    return {
       name: String(o.select('.bookNm').text()).replace(/\（.*\）/,''),
       author:o.select('.authorNm').text(),
       tags:  String(o.select('span:eq(2)').text()).replace(' • ',','),
       num: String(o.select('span:eq(6)').text()).replace(/\s+/g,''),
       latest: String(J.select('.newUpdate a').text())? (String(J.select('.newUpdate a').text()).replace(/[\(（].*[求更谢乐发推].*[）\)]/,'').replace(/\s*，/g,' ')+ '（'+String(J.select('.newUpdate span').text()).replace('更新时间：','')+'）'):'',
       intro: String(o.select('.bookIntro').text()).trim(),     
       cover:o.select('img').attr('data-src').includes('webPic')?'http://suo.im/6mlA9F':o.select('img').attr('data-src'),
       catalogUrl:J.select('.readBtn a:eq(1)').attr('href')
    }
});
</js>

